// Search.js

import React, { useState } from 'react';
import axios from 'axios';

const Search = () => {
  const [searchText, setSearchText] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [searchType, setSearchType] = useState('user'); // Default to searching users

  const handleSearch = async () => {
    try {
      const response = await axios.post('/api/search', { type: searchType, text: searchText });
      setSearchResults(response.data);
    } catch (error) {
      console.error('Error searching:', error);
    }
  };

  return (
    <div>
      <input
        type="text"
        value={searchText}
        onChange={(e) => setSearchText(e.target.value)}
        placeholder="Search..."
      />
      <select value={searchType} onChange={(e) => setSearchType(e.target.value)}>
        <option value="user">Users</option>
        <option value="post">Posts</option>
      </select>
      <button onClick={handleSearch}>Search</button>

      {/* Display search results */}
      <div>
        {searchResults.map((result) => (
          <div key={result._id}>
            {/* Display user or post information */}
            {searchType === 'user' ? (
              <p>User: {result.username}</p>
            ) : (
              <p>Post: {result.content}</p>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Search;
